import React, { useCallback, useEffect, useState } from 'react'
import axios from 'axios';
import Introduction from '../components/Introduction';
import { useAppSelector, useAppDispatch } from '../store/hook';
import { setauthenticatedornot } from '../slices/authenticateornot';
import Sidebar from '../components/Sidebar';
const Home = () => {
    // const open=useAppSelector(state=>state.slider.value);
    const [allproducts, setallproducts] = useState([]);
    const [products, setproducts] = useState([]);
    useEffect(() => {
        const getallproducts = async () => {
            try {
                const response = await axios.get("https://mytaylorzonebackend.onrender.com/get-product", {
                    // withCredentials: true
                });
                console.log(response.data);
                const arr = response.data.products || [];
                setproducts(arr);
                setallproducts(arr);
            } catch (error) {
                console.log(error);
            }
        }
        getallproducts();
    }, []);
    const authenticatedornot = useAppSelector((state) => state.authenticatedornot);
    const dispatch = useAppDispatch();
    console.log(authenticatedornot.auth);
    dispatch(setauthenticatedornot({
        auth: false,
        value: "",
        role: ""
    }))
    // const [allproducts] = React.useState(products);
    const categories = ["All", ...new Set(allproducts.map((item) => item.category))];
    const [...categoriesnew] = categories;
    const [isOpen, setIsOpen] = React.useState(false);
    const onclickcategory = async (category) => {
        console.log(category);
        if (category === "All") {
            setproducts(allproducts);
            setIsOpen(false);
            return
        }
        try {
            let response;
            response = await axios.post(`https://mytaylorzonebackend.onrender.com/product/category`, {
                category: category
            });
            console.log(response.data);
            const arr = response.data.products || [];
            setproducts(arr);
            setIsOpen(false);
        } catch (error) {
            console.log(error);
            setIsOpen(false);
        } finally {
            setIsOpen(false);
        }
    }
    const onsearchproduct = useCallback((e) => {
        const search = e.target.value.toLowerCase();
        const filteredproducts = allproducts.filter((product) => product.name.toLowerCase().includes(search));
        setproducts(filteredproducts);
    }, [
        allproducts
    ]);
    return (
        <div className='homecolor mt-1 relative'>
            <Sidebar/>
            <div
                className="h-screen w-full bg-center bg-no-repeat sm:bg-cover bg-contain"
                style={{ backgroundImage: "url('/face.avif')" }}
            ></div>
            <div>
                <Introduction />
            </div>
            <div className='flex flex-col gap-3'>
                <div className='flex justify-between items-center px-5'>
                    <div className='text-2xl font-bold font-[anzonew]'>Products</div>
                    <div className='mt-2'>
                        <div className="relative inline-block text-left">
                            <div>
                                <button onClick={() => setIsOpen(!isOpen)} type="button" className="inline-flex w-full justify-center gap-x-1.5 font-[anzonew] rounded-md border-black border-2 bg-white px-3 py-2 text-sm font-bold text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 hover:bg-gray-50" id="menu-button" aria-expanded="true" aria-haspopup="true">
                                    Categories
                                    <svg className="-mr-1 size-5 text-gray-400" viewBox="0 0 20 20" fill="currentColor" aria-hidden="true" data-slot="icon">
                                        <path fillRule="evenodd" d="M5.22 8.22a.75.75 0 0 1 1.06 0L10 11.94l3.72-3.72a.75.75 0 1 1 1.06 1.06l-4.25 4.25a.75.75 0 0 1-1.06 0L5.22 9.28a.75.75 0 0 1 0-1.06Z" clipRule="evenodd" />
                                    </svg>
                                </button>
                            </div>
                            {isOpen && <div className="absolute right-0 z-10 mt-2 w-56 origin-top-right rounded-md bg-white shadow-lg ring-1 overflow-y-scroll h-64 ring-black/5 focus:outline-none" role="menu" aria-orientation="vertical" aria-labelledby="menu-button" tabIndex="-1">
                                <div className="py-1 flex flex-col gap-3 group" role="none">
                                    {categoriesnew.map((item) => { if (item == "") return; return (<div onClick={() => onclickcategory(item)} key={item} className="block px-4 py-2 transition-all duration-500 hover:bg-gray-100 font-bold text-sm cursor-pointer  text-gray-700 " role="menuitem" tabIndex="-1" id="menu-item-0">{item}</div>) })}
                                </div>
                            </div>}
                        </div>
                    </div>
                </div>
                <div className='mx-5'>
                    <input onChange={(e) => onsearchproduct(e)} placeholder='Search...' className='w-full p-2 placeholder:text-sm placeholder:font-[anzonew] border-2 border-black rounded-lg' type="text" />
                </div>
                <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 place-items-center p-5 gap-5 transition-all duration-500">

                    {products.map((item) => {
                        if (item.name == "" || item.category == "" || item.price == "" || item.img == "" || item.visibility == false) {
                            return;
                        }
                        if (item.name == "Sample Product") {
                            item.img = "data:image/jpeg;base64,";
                        } else if (item.name == "Women's Summer Dress") {
                            item.img = "data:image/jpeg;base64,"
                        } else if (item.name == "Leather Wallet") {
                            item.img = "data:image/jpeg;base64,";
                        } else if (item.name == "Running Shoes") {
                            item.img = "data:image/jpeg;base64,";
                        } else if (item.name == "Sports Jacket") {
                            item.img = "data:image/jpeg;base64,";
                        } else if (item.name == "Men's T-Shirt") {
                            item.img = "data:image/jpeg;base64,";
                        }
                        return (
                            <div key={item._id} className="eachproduct rounded-lg shadow-md p-4 m-4 w-full border-2 min-h-[200px] flex flex-col items-center justify-center border-black ">
                                {item.img && <img
                                    src={item.img}
                                    alt={item.name}
                                    className="w-full h-48 object-cover mb-4 rounded-lg"
                                />}
                                <h3 className="text-xl font-bold mb-2">{item.name}</h3>
                                <p className="text-gray-700 mb-2">{item.category}</p>
                                {/* <p className="text-gray-700 mb-2">Rating: {item.rating}⭐</p> */}
                                <p className="text-gray-700 font-bold mb-4">{item.price.includes("₹") ? item.price : "₹" + item.price}</p>
                                <button className="buttonhome text-white px-4 py-2 rounded-lg  transition-all duration-300">
                                    Add to Cart
                                </button>
                            </div>
                        )
                    })}


                </div>
            </div>
        </div>
    )
}

export default Home
